import { useNavigation } from "@react-navigation/native";

import { FlashList } from "@shopify/flash-list";

import { useCallback, useEffect, useLayoutEffect, useState } from "react";

import { View, XStack, YStack } from "tamagui";

import { FlatList, ScrollView } from "react-native";

import Itext from "../components/Text/Itext";

import BannerItem from "./components/BannerItem";
import CategoryItem from "./components/CategoryItem";
import { HomeProvider, useHomeContext } from "./provider/HomeProvider";

const banners = [
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
  "data:image/jpeg;base64,",
];

function UIHomeScreen() {
  const navigation = useNavigation();
  const [currentIndex, setCurrentIndex] = useState(0);
  const { category } = useHomeContext();

  useLayoutEffect(
    useCallback(() => {
      navigation.setOptions({
        headerShown: true,
        headerTitle: "Home",
        headerTitleAlign: "left",
        headerShadowVisible: false,
      });
    }, [navigation]),
    []
  );

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % banners.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const _renderItemCategory = ({ item }) => {
    return <CategoryItem item={item} />;
  };

  return (
    <View flex={1} bg="#fff">
      <ScrollView>
        <YStack
          br={16}
          borderColor={"#1a202c1a"}
          borderWidth={1}
          py={10}
          mx={16}
          px={12}
        >
          <Itext text={"TÌm kiếm"} />
        </YStack>

        <YStack als="center" my={16}>
          <BannerItem img={banners[currentIndex]} />
          <XStack gap={8} als="center" my={8}>
            {banners.map((item) => {
              return (
                <View
                  w={6}
                  h={6}
                  br={3}
                  key={item}
                  bg={item === banners[currentIndex] ? "#000" : "#887B72"}
                />
              );
            })}
          </XStack>
        </YStack>
        {/* <Itext text={"Category"} size={18} font={"medium"} /> */}

        <YStack>
          <FlashList
            data={category}
            horizontal
            renderItem={_renderItemCategory}
            ItemSeparatorComponent={() => <View h={16} />}
            estimatedItemSize={155}
          />
        </YStack>

        <YStack mx={16} bg="#ff6d03" flex={1}></YStack>
      </ScrollView>
    </View>
  );
}

function HomeScreen() {
  return (
    <HomeProvider>
      <UIHomeScreen />
    </HomeProvider>
  );
}

export default HomeScreen;
